const groupByData = (dataItems, nameKey, valueKey) => {
    const groupedData = {};
    let id = 0;
    dataItems.forEach((v) => {
        if(groupedData.hasOwnProperty(v[nameKey])) {
        groupedData[v[nameKey]].value += Number(v[valueKey])
        
        } else {
        groupedData[v[nameKey]] = {
        name : v[nameKey],
        value: Number(v[valueKey]),
        id: id
        }
        id += 1
        }
        });
        return groupedData;
}

const idGenerator = () => new Date().toISOString();

module.exports = {
    groupByData,
    idGenerator
}